class SvgIcon extends StatelessWidget {
  final String svg;
  final Color color;
  final double size;
  const SvgIcon(this.svg, {super.key, required this.color, this.size = 24});

  @override
  Widget build(BuildContext context) {
    return SvgPicture.string(
      svg,
      width: size,
      height: size,
      colorFilter: ColorFilter.mode(color, BlendMode.srcIn),
    );
  }
}
